import React, {useEffect, useImperativeHandle, useState} from 'react';
import Floor from './components/Floor';
import Block from './components/Block';
import TileFrame from './components/TileFrame';
import './css/stylesheet.css';

const App = () => {

    const childCompRef = useRef();

    useEffect( () => {
        console.log(block.current.jump());
    })

    const [score, setScore] = useState(0);
    const [highScore, setHighScore] = useState(0);
    const [started, setStarted] = useState(false);
    const [ended, setEnded] = useState(false);
    const [height, setHeight] = useState(100);
    const [bottom, setBottom] = useState(true);
    const [initialTilePos, setInitialTilePos] = useState(50);
    const [firstTilePos, setFirstTilePos] = useState(50);

    return (
        <div className="ui container">
            <Floor setScore={setScore} setHighScore={setHighScore} setStarted={setStarted} setEnded={setEnded}/>
            <Block ref={childCompRef} height={height} setHeight={setHeight} bottom={bottom} setBottom={setBottom} initialTilePos={initialTilePos} firstTilePos={firstTilePos}/>
            <TileFrame bottom={bottom} setInitialTilePos={setInitialTilePos} setFirstTilePos={setFirstTilePos}/>
        </div>
    )
}

export default App;
