import { clear } from '@testing-library/user-event/dist/clear';
import React, {useEffect, useRef} from 'react';
import '../css/stylesheet.css';

const Block = ({height, setHeight, setBottom, initialTilePos}) => {

    const blockRef = useRef();
    let pixelHeight = height + "px";
    const initialPos = (parseInt(initialTilePos) + 7) + "%";

    useEffect( () => {
    }, [pixelHeight])

    useEffect( () => {
        let keydown = false;
        // const timerLat = (event) => setInterval(lateralMovement(event), 10);
        // function lateralMovement(event) {
        //     event.preventDefault();
        //     console.log("working");
        //     // right movement
        //     if (event.keyCode === 39 && event.keyCode !== '37' && parseInt(blockRef.current.style.left) <= 467) {
        //         console.log("right");
        //         blockRef.current.style.left = (parseInt(blockRef.current.style.left) + 2) + "%";
        //     }
        //     // left movement
        //     else if (event.keyCode === 37){ //&& event.keyCode !== '39' && parseInt(blockRef.current.style.left) >= 3) {
        //         console.log("left");
        //         console.log(blockRef.current.style.left);
        //         blockRef.current.style.left = (parseInt(blockRef.current.style.left) - 2) + "%"; 
        //     }
        let timerRight;
        if (!keydown){
            document.addEventListener("keydown", event => {
                keydown = true;
                if (event.keyCode === 39 && event.keyCode !== 37){
                    timerRight = setInterval(moveRight, 100);
                    function moveRight() {
                        console.log("start");
                        if (parseInt(blockRef.current.style.left) <= 92){
                            console.log(parseInt(blockRef.current.style.left))
                            blockRef.current.style.left = (parseInt(blockRef.current.style.left) + 2) + "%";
                        }
                        else {
                            clearInterval(timerRight);
                        }
                    }
                }
            });
        }
        document.addEventListener("keyup", clearInterval(timerRight));

    }, [])

    const jump = () => {
        setBottom(false);
        const jumpHeight = 200;
        let currentHeight = height;
        const startingHeight = height;
        const timerUp = setInterval(jumpFrame, 50);
        function jumpFrame() {
            if (currentHeight - startingHeight >= jumpHeight) {
                clearInterval(timerUp);
                setHeight(currentHeight);
                down();
            }
            else{
                currentHeight += 10;
                pixelHeight = currentHeight + "px"
                blockRef.current.style.bottom = pixelHeight;
            }
        }

        const down = () => {
            const timerDown = setInterval(downFrame, 50);
            function downFrame() {
                if (currentHeight <= 100){
                    clearInterval(timerDown);
                    setHeight(currentHeight);
                    setBottom(true);
                }
                else {
                    currentHeight -= 10;
                    pixelHeight = currentHeight + "px"
                    blockRef.current.style.bottom = pixelHeight;
                }
            }
        }
    }

    return (
        <div ref={blockRef} className="block" onClick={() => {jump()}} style={{bottom: pixelHeight, left: initialPos}}>

        </div>
    )

}

export default Block;
