import React, {useEffect, useState} from 'react';
import Floor from './components/Floor.js';
import Block from './components/Block.js';
import TileFrame from './components/TileFrame.js';
import Score from './components/Score.js';
import NftPage from './components/NftPage.js';
import factory from './blockchain/factory.js';
import signer from './blockchain/ethers.js';
import './css/stylesheet.css';
import { ethers } from 'ethers';

const App = () => {

    const [nft, setNft] = useState(null);
    const [nftScore, setNftScore] = useState(0);
    const [score, setScore] = useState(0);
    const [nftPage, setNftPage] = useState(false);
    const [highScore, setHighScore] = useState(0);
    const [worldHighScore, setWorldHighScore] = useState(0);
    const [worldHighScoreHolder, setWorldHighScoreHolder] = useState(null);
    const [ended, setEnded] = useState(true);
    const [height, setHeight] = useState(100);
    const [bottom, setBottom] = useState(false);
    const [initialTilePos, setInitialTilePos] = useState(0);
    const [firstTilePos, setFirstTilePos] = useState(0);
    const [gameContract, setGameContract] = useState(null);

    const loadGame = async() => {
        const walletAddress = await signer.getAddress();
        const ERC20_ABI = [
            'constructor()',
            'function highScore() public view returns (uint256)',
            'function mint(string memory tokenURI) public returns (uint256)',
            'function setNewHighScore(uint256 newHighScore) external'
        ];
        if (!(await factory.isPlayedBefore(walletAddress))){
            await factory.newJumpingGame();
            setGameContract(gameContract);
        }
        const gameAddress = await factory.playersJumpingGameAddress(walletAddress);
        const gameContract = new ethers.Contract(gameAddress, ERC20_ABI, signer);
        setGameContract(gameContract);
        setHighScore(await gameContract.highScore());
        setWorldHighScore(await factory.worldHighScore());
        setWorldHighScoreHolder(await factory.worldHighScoreHolder());
    }

    useEffect( () => {
        loadGame();
        console.log("game loaded");
    }, [])

    useEffect( () => {
        console.log(ethers.BigNumber.from(highScore).toNumber());
        console.log(ethers.BigNumber.from(worldHighScore).toNumber());
    }, [highScore])

    useEffect( () => {
        startGame();
    }, [ended]);

    const startGame = () => {
        console.log("runing useEffect");

        if (!ended){

            const timer = setTimeout(run, 3000);
            function run() {
                console.log("setting to true");
                setBottom(true);
                clearInterval(timer);
            }
        }
    }

    return (
            <div className="ui container">
                {/* <NftPage/> */}
                <Score setScore={setScore} setEnded={setEnded} setHeight={setHeight} setBottom={setBottom} score={score} ended={ended} setNftPage={setNftPage} bottom={bottom}/>
                <Floor setScore={setScore} setHighScore={setHighScore} setEnded={setEnded}/>
                <TileFrame bottom={bottom} setInitialTilePos={setInitialTilePos} setFirstTilePos={setFirstTilePos}/>
                <Block score={score} setScore={setScore} setEnded={setEnded} height={height} setHeight={setHeight} bottom={bottom} setBottom={setBottom} initialTilePos={initialTilePos} firstTilePos={firstTilePos}/>
            </div>
    )


};

export default App;
