import React, {useEffect, useRef, useState} from 'react';
import '../css/stylesheet.css';

const TileFrame = ({bottom, setInitialTilePos, setFirstTilePos}) => {

    const fifth = useRef();
    const fourth = useRef();
    const third = useRef();
    const second = useRef();
    const first = useRef();

    const [counter, setCounter] = useState(1);


    useEffect( () => {
        fifth.current.style.bottom = "687px";
        fifth.current.style.left = (Math.random() * 80) + "%";
        fourth.current.style.bottom = "536px";
        fourth.current.style.left = (Math.random() * 80) + "%";
        third.current.style.bottom = "389px";
        third.current.style.left = (Math.random() * 80) + "%";
        second.current.style.bottom = "242px";
        second.current.style.left = (Math.random() * 80) + "%";
        first.current.style.bottom = "95px";
        first.current.style.left = (Math.random() * 80) + "%";
        setInitialTilePos(first.current.style.left);
        setFirstTilePos(first.current.style.left);
    }, []);

    useEffect( () => {
        if (!bottom) {
            let total = 147;
            const timerDown = setInterval(downFrame, 100);
            function downFrame() {
                let tile; // Allows us to refer to the tile <div>
                    switch(counter){
                        case 1:
                            tile = first;
                            break;
                        case 2:
                            tile = second;
                            break;
                        case 3:
                            tile = third;
                            break;
                        case 4:
                            tile = fourth;
                            break;
                        case 5:
                            tile = fifth;
                            break;
                    }

                // If the tiles are done moving down
                if (total <= 0) {
                    clearInterval(timerDown);
                    setFirstTilePos(tile.current.style.left);
                    console.log(tile.current.style.left);
                    if (counter == 5) {
                        setCounter(1);
                    }
                    else {
                        setCounter(counter + 1);
                    }
                }
                // Tiles are still moving down
                // This will occur 7 times
                else {
                    total -= 7;
                    // moves the tile to the top when it reaches the bottom
                    console.log(parseInt(tile.current.style.bottom))
                    console.log(tile);
                    if (parseInt(tile.current.style.bottom) < 22) {
                        // tile.current.style.bottom = "750px";
                        tile.current.style.bottom = "676px";
                        tile.current.style.left = (Math.random() * 80) + "%";
                    }
                    // moves all tiles down 21 pixels
                    fifth.current.style.bottom = (parseInt(fifth.current.style.bottom) - 7) + "px";
                    fourth.current.style.bottom = (parseInt(fourth.current.style.bottom) - 7) + "px";
                    third.current.style.bottom = (parseInt(third.current.style.bottom) - 7) + "px";
                    second.current.style.bottom = (parseInt(second.current.style.bottom) - 7) + "px";
                    first.current.style.bottom = (parseInt(first.current.style.bottom) - 7) + "px";
                }
            }
        }
    }, [bottom])

    return (
        <div className="tileframe">
            <div className="tile" ref={fifth}></div>
            <div className="tile" ref={fourth}></div>
            <div className="tile" ref={third}></div>
            <div className="tile" ref={second}></div>
            <div className="tile" ref={first}></div>
        </div>
    )
}

export default TileFrame;
