import { clear } from '@testing-library/user-event/dist/clear';
import React, {useState, useEffect} from 'react';
import '../css/stylesheet.css';

const Block = ({height, setHeight}) => {

    let pixelHeight = height + "px";

    useEffect( () => {
        console.log(height);
        pixelHeight = height + "px";
        console.log(pixelHeight);
    }, [height])

    const jump = () => {
        const jumpHeight = 165;
        const startingHeight = height;
        const timerUp = setInterval(jumpFrame, 10);
        function jumpFrame() {
            while(true) {
                if (jumpHeight < height - startingHeight) {
                    clearInterval(timerUp);
                    console.log('cleared timer');
                    break;
                }
                else{
                    setHeight(height + 33);
                }
            }
        }
    }

    const down = () => {
        const timerDown = setInterval(downFrame, 10);
        const downFrame = () => {
            if (height < 25){
                clearInterval(timerDown);
            }
            else {
                setHeight(height - 55);
            }
        }
    }

    return (
        <div className="block" onClick={() => {jump()}} style={{bottom: pixelHeight}}>

        </div>
    )

}

export default Block;
