import React, {useEffect, useRef} from 'react';
import '../css/stylesheet.css';

const Block = ({height, setHeight, setBottom, initialTilePos, firstTilePos}) => {

    const blockRef = useRef();
    let pixelHeight = height + "px";
    const initialPos = (parseInt(initialTilePos) + 7) + "%";


    useEffect( () => {
        console.log("first Tile Position updated to " + firstTilePos);
        jump();
    }, [firstTilePos])

    useEffect( () => {
        let timerRight;
        let timerLeft;
        let keydown = false;
        document.addEventListener("keydown", event => {
            event.preventDefault();
            if (keydown) {
                clearInterval(timerRight);
                clearInterval(timerLeft);
                keydown = false;
            }
            else {
                keydown = true;
                if (event.keyCode === 39){
                    timerRight = setInterval(moveRight, 20);
                    function moveRight() {
                        if (parseInt(blockRef.current.style.left) <= 93){
                            blockRef.current.style.left = (parseInt(blockRef.current.style.left) + 1) + "%";
                        }
                        else {
                            clearInterval(timerRight);
                            keydown = false;
                        }
                    }
                }
                if (event.keyCode === 37) {
                    timerLeft = setInterval(moveLeft, 20);
                    function moveLeft() {
                        if (parseInt(blockRef.current.style.left) >= 1){
                            blockRef.current.style.left = (parseInt(blockRef.current.style.left) - 1) + "%";
                        }
                        else {
                            clearInterval(timerLeft);
                            keydown = false;
                        }
                    }
                }
            }
        })
    }, []);

    const jump = () => {
        setBottom(false);
        const jumpHeight = 200;
        let currentHeight = height;
        const startingHeight = height;
        const timerUp = setInterval(jumpFrame, 50);
        function jumpFrame() {
            if (currentHeight - startingHeight >= jumpHeight) {
                clearInterval(timerUp);
                setHeight(currentHeight);
                down();
            }
            else{
                currentHeight += 10;
                pixelHeight = currentHeight + "px"
                blockRef.current.style.bottom = pixelHeight;
            }
        }

        const down = () => {
            const timerDown = setInterval(downFrame, 50);
            function downFrame() {
                if (currentHeight <= 100){
                    clearInterval(timerDown);
                    setHeight(currentHeight);
                    setBottom(true);
                    let blockPos = parseInt(blockRef.current.style.left);
                    console.log("firstTilePos = " + firstTilePos);
                    console.log("block pos = " + blockPos);
                    if(firstTilePos - 6 < blockPos && blockPos < firstTilePos + 14 + 6){
                        console.log("if statement worked");
                        jump();
                    }
                }
                else {
                    currentHeight -= 10;
                    pixelHeight = currentHeight + "px"
                    blockRef.current.style.bottom = pixelHeight;
                }
            }
        }
    }

    return (
        <div ref={blockRef} className="block" onClick={() => {jump()}} style={{bottom: pixelHeight, left: initialPos}}>

        </div>
    )

}

export default Block;
