import { clear } from '@testing-library/user-event/dist/clear';
import React, {useState, useEffect, useRef} from 'react';
import '../css/stylesheet.css';

const Block = ({height, setHeight}) => {

    const blockRef = useRef();
    let pixelHeight = height + "px";

    useEffect( () => {
    }, [pixelHeight])

    const jump = () => {
        const jumpHeight = 165;
        let currentHeight = height;
        const startingHeight = height;
        const timerUp = setInterval(jumpFrame, 1000);
        function jumpFrame() {
            if (currentHeight - startingHeight >= jumpHeight) {
                clearInterval(timerUp);
                console.log('cleared timer');
            }
            else{
                currentHeight += 33;
                pixelHeight = currentHeight + "px"
                console.log(pixelHeight);
                blockRef.current.bottom = pixelHeight;
            }
        }
    }

    const down = () => {
        const timerDown = setInterval(downFrame, 10);
        const downFrame = () => {
            if (height < 25){
                clearInterval(timerDown);
            }
            else {
                setHeight(height - 55);
            }
        }
    }

    return (
        <div ref={blockRef} className="block" onClick={() => {jump()}} style={{bottom: pixelHeight}}>

        </div>
    )

}

export default Block;
