import React, {useEffect, useRef, forwardRef, useImperativeHandle} from 'react';
import '../css/stylesheet.css';

const Tile = forwardRef((props, forwardedRef) => {

    const tile = useRef();

    useImperativeHandle(forwardedRef, () => ({
        moveDown() {
            let total = 147;
            const timerDown = setInterval(downFrame, 100);
            function downFrame() {

                const height = parseInt(tile.current.style.bottom);
                total -= 7;

                if (total >= 0) {
                    if (height > 15) {
                        tile.current.style.bottom = (height - 7) + "px";
                    }
                    else {
                        tile.current.style.bottom = "594px";
                    }
                }
                else {
                    clearInterval(timerDown);
                }
            }
        },
        isBottom() {
            const height = parseInt(tile.current.style.bottom);
            if (height === 237){
                console.log("setting firstTilePos");
                console.log(height);
                console.log(parseInt(tile.current.style.left));
                props.setFirstTilePos(parseInt(tile.current.style.left));
            }
        },
        getStyle() {
            return tile.current.style;
        }
    }))

    return (
        <div className="tile" ref={tile} ></div>
    )
})

export default Tile;
