import { FixedNumber } from 'ethers';
import React, { useEffect } from 'react';
import signer from '../blockchain/ethers.js';
import factory from '../blockchain/factory.js';
import { ethers } from 'ethers';
import '../css/stylesheet.css';

const Score = ({gameContract, highScore, worldHighScore, worldHighScoreHolder, setScore, setEnded, setHeight, setBottom, score, ended, setNftPage, bottom}) => {

    const updateScores = async() => {
        if (score > highScore && score > worldHighScore) {
            if (window.confirm("Congratulations you've betten the high score!!!\n\nWould you like to update the high score and world high score?")) {
                await gameContract.setNewHighScore(score);
                await factory.updateHighScore(score);

            }
            else if (window.confirm("Okay sure thing, do you at least want to update your own high score?")) {
                await gameContract.setNewHighScore(score);
            }
        }
        else if (score > highScore) {
            if (window.confirm("Congratulations on your new high score!\n\nWould you like to update your high score?")){
                await gameContract.setNewHighScore(score);
            }
        }
    }

    useEffect(() => {
        console.log(score);
        console.log(highScore);
        console.log(worldHighScore);
        console.log(worldHighScoreHolder);
        console.log(gameContract);
    }, [score, ended])

    if (!ended){
        return (
            <>
                <div className="worldhighscore_div">
                    World High Score {worldHighScore}
                </div>
                <div className="highscore_div">
                    High Score {highScore}
                </div>
                <div className="score_div">
                    {score}
                </div>
            </>
        )
    }
    else if (bottom) {
        return(
            <>
                <div className="worldhighscore_div">
                    World High Score {worldHighScore} 
                </div>
                <div className="worldhighscoreholder_div">
                    World High Score Holder {worldHighScoreHolder}
                </div>
                <div className="highscore_div">
                    High Score {highScore}
                </div>
                <div className="score_div">
                    {score}
                </div>
                <div className="gameover">
                    You're Trash
                </div>
                <button type="button" className="nft_button" onClick={ event => {
                    event.preventDefault();
                    setNftPage(true);
                }}>
                   Get NFT!
                </button>
                <button type="button" className="play_again_button1" onClick={ event => {
                    event.preventDefault();
                    setScore(0);
                    setEnded(false);
                    setHeight(100);
                    setBottom(false);
                }}>
                   Play
                </button>
            </>
        )
    }
    else {
        return(
            <>
                <div className="worldhighscore_div">
                    World High Score {worldHighScore}
                </div>
                <div className="worldhighscoreholder_div">
                    World High Score Holder {worldHighScoreHolder}
                </div>
                <div className="highscore_div">
                    High Score {highScore}
                </div>
                <div className="score_div">
                    {score}
                </div>
                <button type="button" className="nft_button" onClick={ event => {
                    event.preventDefault();
                    const delay1 = setTimeout(changeNftPage1, 800);
                    function changeNftPage1() {
                        setNftPage(true);
                        clearInterval(delay1);
                    };
                }}>
                   Get NFT!
                </button>
                <button type="button" className="play_again_button1" onClick={ event => {
                    event.preventDefault();
                    setScore(0);
                    setEnded(false);
                    setHeight(100);
                    setBottom(false);
                }}>
                   Play
                </button>
            </>
        )
    }
}

export default Score;
