import React, {useEffect, useRef, useState} from 'react';
import '../css/stylesheet.css';

const TileFrame = ({bottom, setInitialTilePos, setFirstTilePos}) => {

    const fifth = useRef();
    const fourth = useRef();
    const third = useRef();
    const second = useRef();
    const first = useRef();

    let bottomTileString;
    let fifthBottom;
    let fourthBottom;
    let thirdBottom;
    let secondBottom;
    let firstBottom;

    const [bottomTile, setBottomTile] = useState(undefined);

    useEffect( () => {
        fifth.current.style.bottom = "678px";
        fifth.current.style.left = (Math.random() * 80) + "%";
        fourth.current.style.bottom = "531px";
        fourth.current.style.left = (Math.random() * 80) + "%";
        third.current.style.bottom = "384px";
        third.current.style.left = (Math.random() * 80) + "%";
        second.current.style.bottom = "237px";
        second.current.style.left = (Math.random() * 80) + "%";
        first.current.style.bottom = "90px";
        first.current.style.left = (Math.random() * 80) + "%";
        setInitialTilePos(first.current.style.left);
        setFirstTilePos(first.current.style.left);
        console.log("running");

        // Allows us to refer to the tile <div>
        let fifthBottom = parseInt(fifth.current.style.bottom);
        let fourthBottom = parseInt(fourth.current.style.bottom);
        let thirdBottom = parseInt(third.current.style.bottom);
        let secondBottom = parseInt(second.current.style.bottom);
        let firstBottom = parseInt(first.current.style.bottom);

        const bottoms = {
            "fifth": fifthBottom,
            "fourth": fourthBottom,
            "third": thirdBottom,
            "second": secondBottom,
            "first": firstBottom,
        }

        bottomTileString = "";
        for (let key in bottoms) {
            if (bottomTileString !== '' && bottoms[key] < bottoms[bottomTileString]) {
                bottomTileString = key;
            } else if (bottomTileString === "") {
                bottomTileString = key;
            }
        }

        switch(bottomTileString){
            case 'first':
                setBottomTile(first);
                break;
            case 'second':
                setBottomTile(second);
                break;
            case 'third':
                setBottomTile(third);
                break;
            case 'fourth':
                setBottomTile(fourth);
                break;
            case 'fifth':
                setBottomTile(fifth);
                break;
        }
    }, []);



    let removed = true;
    useEffect( () => {
        if (!bottom) {
            let total = 147;
            const timerDown = setInterval(downFrame, 100);
            function downFrame() {
                // If the tiles are done moving down
                if (total <= 0) {
                    removed = false;
                    setFirstTilePos(bottomTile.current.style.left);
                    console.log(total);
                    clearInterval(timerDown);
                }
                // Tiles are still moving down
                else if (parseInt(bottomTile.current.style.bottom) < 20) {                        
                    bottomTile.current.style.bottom = "678px";
                    bottomTile.current.style.left = (Math.random() * 80) + "%";
                    removed = true;
                }
                else {
                    // Allows us to refer to the tile <div>
                    let fifthBottom = parseInt(fifth.current.style.bottom);
                    let fourthBottom = parseInt(fourth.current.style.bottom);
                    let thirdBottom = parseInt(third.current.style.bottom);
                    let secondBottom = parseInt(second.current.style.bottom);
                    let firstBottom = parseInt(first.current.style.bottom);

                    const bottoms = {
                        "fifth": fifthBottom,
                        "fourth": fourthBottom,
                        "third": thirdBottom,
                        "second": secondBottom,
                        "first": firstBottom,
                    }

                    bottomTileString = "";
                    for (let key in bottoms) {
                        if (bottomTileString !== '' && bottoms[key] < bottoms[bottomTileString]) {
                            bottomTileString = key;
                        } else if (bottomTileString === "") {
                            bottomTileString = key;
                        }
                    }

                    switch(bottomTileString){
                        case 'first':
                            setBottomTile(first);
                            break;
                        case 'second':
                            setBottomTile(second);
                            break;
                        case 'third':
                            setBottomTile(third);
                            break;
                        case 'fourth':
                            setBottomTile(fourth);
                            break;
                        case 'fifth':
                            setBottomTile(fifth);
                            break;
                    }
                }
                // This will occur 7 times 
                total -= 7;
                // moves all tiles down 21 pixel
                if (removed) {
                    switch(bottomTileString){
                        case 'first':
                            console.log("all but 1")
                            console.log(fifthBottom);
                            fifth.current.style.bottom = (fifthBottom - 7) + "px";
                            fourth.current.style.bottom = (fourthBottom - 7) + "px";
                            third.current.style.bottom = (thirdBottom - 7) + "px";
                            second.current.style.bottom = (secondBottom - 7) + "px";
                            break;
                        case 'second':
                            console.log("all but 2")
                            fifth.current.style.bottom = (fifthBottom - 7) + "px";
                            fourth.current.style.bottom = (fourthBottom - 7) + "px";
                            third.current.style.bottom = (thirdBottom - 7) + "px";
                            first.current.style.bottom = (firstBottom - 7) + "px";
                            break;
                        case 'third':
                            console.log("all but 3")
                            fifth.current.style.bottom = (fifthBottom - 7) + "px";
                            fourth.current.style.bottom = (fourthBottom - 7) + "px";
                            second.current.style.bottom = (secondBottom - 7) + "px";
                            first.current.style.bottom = (firstBottom - 7) + "px";
                            break;
                        case 'fourth':
                            fifth.current.style.bottom = (fifthBottom - 7) + "px";
                            third.current.style.bottom = (thirdBottom - 7) + "px";
                            second.current.style.bottom = (secondBottom - 7) + "px";
                            first.current.style.bottom = (firstBottom - 7) + "px";
                            break;
                        case 'fifth':
                            fourth.current.style.bottom = (fourthBottom - 7) + "px";
                            third.current.style.bottom = (thirdBottom - 7) + "px";
                            second.current.style.bottom = (secondBottom - 7) + "px";
                            first.current.style.bottom = (firstBottom - 7) + "px";
                            break;
                    }
                }
            else {
                // moves the tile to the top when it reaches the bottom
                fifth.current.style.bottom = (fifthBottom - 7) + "px";
                fourth.current.style.bottom = (fourthBottom - 7) + "px";
                third.current.style.bottom = (thirdBottom - 7) + "px";
                second.current.style.bottom = (secondBottom - 7) + "px";
                first.current.style.bottom = (firstBottom - 7) + "px";
            }
            }
        }
    }, [bottom])

    return (
        <div className="tileframe">
            <div className="tile" ref={fifth}></div>
            <div className="tile" ref={fourth}></div>
            <div className="tile" ref={third}></div>
            <div className="tile" ref={second}></div>
            <div className="tile" ref={first}></div>
        </div>
    )
}

export default TileFrame;
