import { clear } from '@testing-library/user-event/dist/clear';
import React, {useState} from 'react';
import '../css/stylesheet.css';

const Block = ({height, setHeight}) => {

    // useEffect( () => {

    // }, [height])

    const jump = () => {
        const jumpHeight = 165;
        const startingHeight = height;
        const timerUp = setInterval(frame, 10);
        const frame = () => {
            if (jumpHeight >= height - startingHeight) {
                clearInterval(timerUp);
            }
            else{
                setHeight(height + 55);
            }
        }
        down();
    }

    const down = () => {
        const timerDown = setInterval(frame, 10);
        const frame = () => {
            if (height < 25){
                clearInterval(timerDown);
            }
            else {
                setHeight(height - 55);
            }
        }
    }

    return (
        <div className="block" onClick={() => {setHeight(height + 10)}} style={{bottom: height}}>

        </div>
    )

}

export default Block;
