import React, {useEffect, useRef, useState} from 'react';
import '../css/stylesheet.css';

const TileFrame = ({bottom}) => {

    const fifth = useRef();
    const fourth = useRef();
    const third = useRef();
    const second = useRef();
    const first = useRef();

    const [counter, setCounter] = useState(1);


    useEffect( () => {
        fifth.current.style.bottom = "687px";
        fifth.current.style.left = (Math.random() * 80) + "%";
        fourth.current.style.bottom = "536px";
        fourth.current.style.left = (Math.random() * 80) + "%";
        third.current.style.bottom = "389px";
        third.current.style.left = (Math.random() * 80) + "%";
        second.current.style.bottom = "242px";
        second.current.style.left = (Math.random() * 80) + "%";
        first.current.style.bottom = "95px";
        first.current.style.left = (Math.random() * 80) + "%";
        console.log(first.current.style.left)
    }, []);

    useEffect( () => {
        if (!bottom) {
            let total = 147;
            const timerDown = setInterval(downFrame, 100);
            function downFrame() {
                if (total <= 0) {
                    clearInterval(timerDown);
                    if (counter == 5) {
                        setCounter(1);
                    }
                    else {
                        setCounter(counter + 1);
                    }
                }
                else {
                    total -= 21;
                    let tile;
                    switch(counter){
                        case 1:
                            tile = first;
                            break;
                        case 2:
                            tile = second;
                            break;
                        case 3:
                            tile = third;
                            break;
                        case 4:
                            tile = fourth;
                            break;
                        case 5:
                            tile = fifth;
                            break;
                    }

                    if (parseInt(tile.current.style.bottom) < 20) {
                        tile.current.style.bottom = "743px";
                        tile.current.style.left = (Math.random() * 80) + "%";
                    }
                    fifth.current.style.bottom = (parseInt(fifth.current.style.bottom) - 21) + "px";
                    fourth.current.style.bottom = (parseInt(fourth.current.style.bottom) - 21) + "px";
                    third.current.style.bottom = (parseInt(third.current.style.bottom) - 21) + "px";
                    second.current.style.bottom = (parseInt(second.current.style.bottom) - 21) + "px";
                    first.current.style.bottom = (parseInt(first.current.style.bottom) - 21) + "px";
                }
            }
        }
    }, [bottom])

    return (
        <div className="tileframe">
            <div className="tile" ref={fifth}></div>
            <div className="tile" ref={fourth}></div>
            <div className="tile" ref={third}></div>
            <div className="tile" ref={second}></div>
            <div className="tile" ref={first}></div>
        </div>
    )
}

export default TileFrame;
